import { Component, OnInit } from '@angular/core';
import { persona } from 'src/app/model/persona.model';
import { PersonaService } from 'src/app/service/persona.service';

@Component({
  selector: 'app-sobre-my',
  templateUrl: './sobre-my.component.html',
  styleUrls: ['./sobre-my.component.css']
})
export class SobreMyComponent implements OnInit {
    persona: persona=new persona("","","");

  constructor(public personaService: PersonaService){}

  ngOnInit(): void {
    this.personaService.getPersonas().subscribe(data =>{this.persona = data})
  }

}
<div class="row">
  <div class="col-xs-12 col-sm-2 col-md-2 col-lg-2">
    <img src="../../../assets/img/jesi.png" class="img-fluid" id="perfil" />
  </div>
  <div class="col-xs-12 col-sm-4 col-md-4 col-lg-4">
    <h1></h1>
    <h2>Full Stack Developer Jr.</h2>
  </div>
  <div class="col-xs-12 col-sm-6 col-md-6 col-lg-6">
    <span>
      <h2>Acerca de mi</h2>
      <p>
        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Vel tenetur
        nam adipisci aut, impedit id inventore? Adipisci facere exercitationem
        corrupti labore, inventore maiores ipsa ratione id officiis blanditiis,
        nostrum molestiae?
      </p>
    </span>
  </div>
</div>
